css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: Arial, sans-serif;
  background-color: #f7f7f7;
}

.plans-section {
  text-align: center;
  padding: 50px 0;
}

.plans-container {
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-top: 30px;
}

.plan-card {
  background-color: white;
  padding: 30px;
  border-radius: 10px;
  width: 300px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s;
}

.plan-card:hover {
  transform: scale(1.05);
}

.plan-card.featured {
  background-color: #f0f0f5;
  border: 2px solid #007bff;
}

h1 {
  color: #333;
  font-size: 36px;
}

h2 {
  font-size: 24px;
  color: #007bff;
}

.price {
  font-size: 36px;
  margin: 20px 0;
}

.price span {
  font-size: 16px;
  color: #777;
}

ul {
  list-style: none;
  margin: 20px 0;
}

ul li {
  margin-bottom: 10px;
  color: #555;
}

button {
  padding: 1px 1px;
  background-color: #FFFFFF;
  color: white;
  border: #FFFFFF;
  border-radius: 10px;
  cursor: pointer;
  transition: background-color 0.3s;
}

button:hover {
  background-color: #FFFFFF;
}

javascript:
const planButtons = document.querySelectorAll('.select-plan');

planButtons.forEach((button, index) => {
  button.addEventListener('click', () => {
    const planDetails = [
      "Basic Plan: $10/month, 5 Users, 10 GB Storage",
      "Standard Plan: $30/month, 15 Users, 50 GB Storage",
      "Premium Plan: $60/month, Unlimited Users, 200 GB Storage"
    ];

    const contactPageURL = `http://www.zoserp.com/contact?message=${encodeURIComponent(planDetails[index])}`;
    window.location.href = contactPageURL;
  });
});
